  setAddress(address, encryption) {
    this._address = address;
    this._encryption = encryption;
    this._socket.close();
    this._socket = new WebSocket(`${encryption ? 'wss' : 'ws'}://${address}`);
    this._socket.onopen = this._onopen.bind(this);
    this._socket.onmessage = this._onmessage.bind(this);
    this._socket.onerror = this._onerror.bind(this);
    this._socket.onclose = this._onclose.bind(this);
  }

  addListener(method, callback) {
    if (!this._listeners[method]) {
      this._listeners[method] = [];
    }
    this._listeners[method].push(callback);
  }

  removeListener(method) {
    delete this._listeners[method];
  }

  request(method, params, id, successCallback, errorCallback) {
    const data = this._formatData(method, params, id);
    this._send(data);
    this._handles[id] = { success: successCallback, error: errorCallback };
  }

  batchRequest(requests, successCallback, errorCallback) {
    requests.forEach(req => {
      this.request(req.method, req.params, req.id, successCallback, errorCallback);
    });
  }

  _formatData(method, params, id) {
    return {
      jsonrpc: '2.0',
      method: this._namespace ? `${this._namespace}.${method}` : method,
      params: params,
      id: id
    };
  }

  _send(data) {
    if (this._socket.readyState === WebSocket.OPEN) {
      this._socket.send(JSON.stringify(data));
    }
  }

  _onerror(event) {
    for (const id in this._handles) {
      const handle = this._handles[id];
      if (handle.error) {
        handle.error(event);
      }
    }
  }

  _onmessage(event) {
    try {
      const data = JSON.parse(event.data);
      if (data.jsonrpc === '2.0') {
        if (data.id !== undefined) {
          const handle = this._handles[data.id];
          if (handle) {
            if (data.error) {
              handle.error(data.error);
            } else {
