            }
            delete this._handles[data.id];
          }
        } else {
          const method = data.method;
          if (this._listeners[method]) {
            this._listeners[method].forEach(cb => cb(data.params));
          }
        }
      }
    } catch (e) {}
  }

  _onopen() {}

