    this.reconnectDecay = options && options.reconnectDecay || 1.5;
    this.timeoutInterval = options && options.timeoutInterval || 2000;
    this.reconnectAttempts = 0;
    this.readyState = WebSocket.CLOSED;
    this.protocol = '';
    this.userClosed = false;
    this.reconnectTimeout = null;
    this.events = {
      open: [],
      close: [],
      connecting: [],
      message: [],
      error: []
    };
    this.onopen = null;
    this.onclose = null;
    this.onconnecting = null;
    this.onmessage = null;
    this.onerror = null;

    if (this.automaticOpen) {
      this.open();
    }
  }

  addEventListener(type, listener) {
    if (!this.events[type]) {
      this.events[type] = [];
    }
    this.events[type].push(listener);
  }

  removeEventListener(type, listener) {
    if (this.events[type]) {
      const index = this.events[type].indexOf(listener);
      if (index !== -1) {
        this.events[type].splice(index, 1);
      }
    }
  }

  dispatchEvent(event) {
    const listeners = this.events[event.type];
    if (listeners) {
      for (const listener of listeners) {
        listener(event);
      }
    }
  }

  open(reconnecting) {
    if (this.readyState === WebSocket.OPEN || this.readyState === WebSocket.CONNECTING) {
      return;
    }

    this.readyState = WebSocket.CONNECTING;
    this.userClosed = false;

    this.dispatchEvent({ type: 'connecting' });
    if (this.onconnecting) this.onconnecting();

    this.socket = new WebSocket(this.url, this.protocols);

    const connectTimeout = setTimeout(() => {
      if (this.readyState === WebSocket.CONNECTING) {
        this.readyState = WebSocket.CLOSED;
        this.dispatchEvent({ type: 'error', message: 'Connection timeout' });
        if (this.onerror) this.onerror(new Event('error'));
        this.reconnectAttempts++;
        const nextDelay = Math.min(
          this.reconnectInterval * Math.pow(this.reconnectDecay, this.reconnectAttempts),
          this.maxReconnectInterval
        );
        this.reconnectTimeout = setTimeout(() => {
          this.open(true);
        }, nextDelay);
      }
    }, this.timeoutInterval);

    this.socket.onopen = (event) => {
      clearTimeout(connectTimeout);
      this.readyState = WebSocket.OPEN;
      this.protocol = this.socket.protocol;
      this.reconnectAttempts = 0;
      this.dispatchEvent({ type: 'open', event });
      if (this.onopen) this.onopen(event);
    };

    this.socket.onmessage = (event) => {
      this.dispatchEvent({ type: 'message', event });
      if (this.onmessage) this.onmessage(event);
    };

    this.socket.onerror = (event) => {
      this.dispatchEvent({ type: 'error', event });
      if (this.onerror) this.onerror(event);
    };

    this.socket.onclose = (event) => {
      this.readyState = WebSocket.CLOSED;
      if (this.userClosed) {
        this.dispatchEvent({ type: 'close', event });
        if (this.onclose) this.onclose(event);
        return;
      }

      this.reconnectAttempts++;
      const nextDelay = Math.min(
        this.reconnectInterval * Math.pow(this.reconnectDecay, this.reconnectAttempts),
        this.maxReconnectInterval
      );
      this.reconnectTimeout = setTimeout(() => {
        this.open(true);
      }, nextDelay);
    };
  }

  send(data) {
    if (this.readyState === WebSocket.OPEN) {
      this.socket.send(data);
    } else {
      if (this.debug) {
        console.warn('WebSocket not open, cannot send data');
      }
    }
  }

  close(code, reason) {
    this.userClosed = true;
    if (this.socket) {
      this.socket.close(code, reason);
    }
    if (this.reconnectTimeout) {
      clearTimeout(this.reconnectTimeout);
      this.reconnectTimeout = null;
    }
    this.readyState = WebSocket.CLOSING;
    this.dispatchEvent({ type: 'close' });
    if (this.onclose) this.onclose();
  }

  refresh() {
    this.close();
    this.open();
  }

  onopen() {}
  onclose() {}
  onconnecting() {}
  onmessage() {}
  onerror() {}
}

