class WebSocketStore {
  constructor(snack, currentUser) {
    this.wsActive = false;
    this.ws = null;
    this.snack = snack;
    this.currentUser = currentUser;
    this.isUserClosed = false;
  }

  listen(callback) {
    if (this.wsActive) return;

    const url = this.currentUser.getWebSocketUrl();
    this.ws = new WebSocket(url);

    this.wsActive = true;
    this.isUserClosed = false;

    this.ws.onopen = () => {
      this.snack.show('WebSocket connected');
    };

    this.ws.onmessage = (event) => {
      try {
        const msg = JSON.parse(event.data);
        callback(msg);
      } catch (e) {
        console.error('Failed to parse message:', e);
      }
    };

    this.ws.onerror = (error) => {
      console.error('WebSocket error:', error);
      this.snack.show('WebSocket error occurred');
      this.reconnect();
    };

    this.ws.onclose = () => {
      this.wsActive = false;
      if (!this.isUserClosed) {
        this.reconnect();
      }
      this.isUserClosed = false;
    };
  }

  close() {
