class WebSocketRPC {
  constructor() {
    this._ = _;
    this.JSON = JSON;
    this.utils = utils;
    this.alerts = alerts;
    this.initialized = false;
    this.handles = [];
    this.sock = null;
    this.conf = null;
    this.scheme = '';
    this.onready = null;
  }

  onerror(ev) {
    this.alerts.error('WebSocket error', ev);
    this.handles.forEach(handle => handle.error(ev));
    this.handles = [];
    this.initialized = false;
    this.sock = null;
  }

  onclose(ev) {
    this.alerts.info('WebSocket closed', ev);
    this.handles.forEach(handle => handle.error(ev));
    this.handles = [];
    this.initialized = false;
    this.sock = null;
  }

  onopen() {
    this.initialized = true;
    if (this.onready) this.onready();
  }

  onmessage(message) {
    try {
      const data = this.JSON.parse(message.data);
      if (data.jsonrpc === '2.0' && data.id) {
        const handle = this.handles.find(h => h.id === data.id);
        if (handle) {
          if (data.result) {
            handle.success(data.result);
          } else if (data.error) {
            handle.error(data.error);
          }
          this.handles = this.handles.filter(h => h.id !== data.id);
        }
      }
    } catch (e) {
      this.alerts.error('Failed to parse message', e);
    }
  }

  invoke(opts) {
    const id = this.utils.uuid();
    const request = {
      jsonrpc: '2.0',
      method: opts.name,
      params: opts.params,
      id: id
    };
    this.handles.push({ id: id, success: opts.success, error: opts.error });
    this.sock.send(this.JSON.stringify(request));
  }

  init(conf, onready) {
    this.conf = conf;
    this.onready = onready;
    this.scheme = conf.encrypt ? 'wss' : 'ws';
    let url = `${this.scheme}://${conf.host}:${conf.port}`;
    if (conf.path) {
      url += conf.path;
    }
    if (conf.auth) {
      url += `?user=${encodeURIComponent(conf.auth.user)}&pass=${encodeURIComponent(conf.auth.pass)}`;
    }
    this.sock = new WebSocket(url);
    this.sock.onopen = () => this.onopen();
    this.sock.onmessage = (event) => this.onmessage(event);
    this.sock.onclose = (event) => this.onclose(event);
    this.sock.onerror = (event) => this.onerror(event);
  }
}

