      const isVisible = document.hasFocus();
      if (this.windowVisible !== isVisible) {
        this.windowVisible = isVisible;
        this.reportFocus(isVisible);
      }
    }, 1000);
    window.addEventListener('focus', this.handleWindowFocus);
    window.addEventListener('blur', this.handleWindowBlur);
  }

  stopFocusLoop() {
    if (this.focusDetectorTimer) {
      clearInterval(this.focusDetectorTimer);
      this.focusDetectorTimer = null;
    }
    window.removeEventListener('focus', this.handleWindowFocus);
    window.removeEventListener('blur', this.handleWindowBlur);
  }

  handleWindowFocus() {
    this.windowVisible = true;
    this.reportFocus(true);
  }

  handleWindowBlur() {
    this.windowVisible = false;
    this.reportFocus(false);
  }
}

