      this.ws.onclose = () => this.ev.emit('close');
      return true;
    } catch (e) {
      return false;
    }
  }

  async wsInvoke(method: string, path: string, querystring?: Record<string, any>): Promise<{ status: number, body: any }> {
    if (!this.ws || this.ws.readyState !== WebSocket.OPEN) {
      throw new Error('WebSocket not connected');
    }
    const payload = { method, path, query: querystring, token: this.token };
    return new Promise((resolve, reject) => {
      const handler = (data: string) => {
        try {
          const res = JSON.parse(data);
          this.ev.off('message', handler);
          resolve({ status: res.status, body: res.body });
        } catch (e) {
          reject(e);
        }
      };
      this.ev.on('message', handler);
      this.ws!.send(JSON.stringify(payload));
    });
  }
