```javascript
const WebSocket = require('ws');

class SnapdropServer {
  constructor(port) {
    this._wss = new WebSocket.Server({ port });
    this._rooms = {};
    this._wss.on('connection', (socket, request) => this._onConnection(socket, request));
    this._wss.on('headers', (headers, request) => this._onHeaders(headers, request));
    this._wss.on('error', (err) => console.error('Server error:', err));
  }

  _onConnection(peer, request) {
    const ip = request.socket.remoteAddress;
    this._joinRoom(peer, ip);
    peer.on('message', (message) => this._onMessage(peer, message.toString()));
    peer.on('close', () => this._leaveRoom(peer, ip));
    this._keepAlive(peer);
  }

  _onHeaders(headers, request) {
    // Example: Set a cookie if needed
    headers['set-cookie'] = 'session=123456';
  }

  _onMessage(peer, message) {
    try {
      const data = JSON.parse(message);
      if (data.type === 'offer' || data.type === 'answer' || data.type === 'candidate') {
        const room = this._getRoomForPeer(peer);
        if (room) {
          this._broadcastToRoom(room, data);
        }
      }
    } catch (e) {
      console.error('Error parsing message:', e);
    }
  }

  _send(peer, message) {
    peer.send(JSON.stringify(message));
  }

  _joinRoom(peer, room) {
    if (!this._rooms[room]) {
      this._rooms[room] = [];
    }
    this._rooms[room].push(peer);
  }

  _leaveRoom(peer, room) {
    if (this._rooms[room]) {
      this._rooms[room] = this._rooms[room].filter(p => p !== peer);
    }
  }

  _keepAlive(peer) {
    setInterval(() => {
      if (peer.readyState === WebSocket.OPEN) {
        peer.send(JSON.stringify({ type: 'ping' }));
      }
    }, 30000);
  }

  _getRoomForPeer(peer) {
    for (const room in this._rooms) {
      if (this._rooms[room].includes(peer)) {
        return room;
      }
    }
    return null;
  }

  _broadcastToRoom(room, message) {
    this._rooms[room].forEach(peer => {
      if (peer.readyState === WebSocket.OPEN) {
        peer.send(JSON.stringify(message));
      }
    });
  }
}
