      xhr.responseType = 'blob';
      xhr.onload = () => {
        if (xhr.status === 200) {
          resolve(xhr.response);
        } else {
          reject(new Error('Download failed'));
        }
      };
      xhr.onerror = () => reject(new Error('Network error'));
      xhr.send();
    });
  }

  request(url: string, options: TRequestOption): Promise<any> {
    const { method, headers, data, requestType } = options;
    if (!['get', 'put', 'post'].includes(method)) {
      throw new Error('Unsupported request method');
    }
    return this.requestMethod(url, {
      method,
      headers,
      data,
      requestType
    });
  }
}

