    app.use(pathKey, createProxyMiddleware({ target, changeOrigin: true }));
  }

  app.use(express.static(path.resolve(config.dev.publicPath)));

  app.use(historyApiFallback({
    index: '/index.html',
    rewrites: [
      { from: /\/$/, to: '/index.html' },
