  constructor(options) {
    this.app = express();
    this.app.disable('x-powered-by');

    // Standard Middleware
    this.app.use(express.json());
    this.app.use(express.urlencoded({ extended: true }));

    // Operations
    this.operations = {};
    this.operationsArray = [];

    // Define routes
    this.app.get('/', this.sendIndex.bind(this));
    this.app.get('/operations', (req, res) => {
      res.send(this.operationsArray);
    });

    // Default Operation
    this.app.get('/:thing/:from', (req, res) => {
      const message = `Fuck ${req.params.thing}.`;
      const subtitle = `- ${req.params.from}`;
      this.output(req, res, message, subtitle);
    });
  }

  sendIndex(req, res) {
    res.sendFile('./public/index.html');
  }

  start(port) {
    this.app.listen(port);
    console.log(`FOAAS Started on port ${port}`);

    request(`http://localhost:${port}/operations`, (error, response, body) => {
      if (error) return console.log('Could not retrieve operations');
      const ops = JSON.parse(body);

      ops.forEach((op) => {
        request({
          url: `http://localhost:${port}${op.url}`,
          headers: { Accept: 'text/plain' }
        }, (error, response, body) => {
          if (error) return console.log('Could not retrieve operation ' + op.url);
        });
      });
    });
  }

  output(req, res, message, subtitle) {
    req.message = message;
    req.subtitle = subtitle;

    // Process output
    res.send(`${message} ${subtitle}`);
  }
