const express = require('express');
const rest = require('connect-rest');
const bodyParser = require('body-parser');
const mongoose = require('mongoose');
const domain = require('domain');
const cluster = require('cluster');
const http = require('http');

function startServer() {
  const app = express();
  app.use(bodyParser.json());
  app.use(bodyParser.urlencoded({ extended: true }));

  const router = rest();
  router.get('/attractions', (req, res) => {
    Attraction.find({ approved: true }, (err, attractions) => {
      if (err) return res.status(500).send(err);
      res.json(attractions);
    });
  });

  router.post('/attraction', (req, res) => {
    const { name, description, location, email } = req.body;
    if (!name || !description || !location || !email) {
      return res.status(400).send('Missing required fields');
    }
    const newAttraction = new Attraction({ name, description, location, email, approved: false });
    newAttraction.save((err, attraction) => {
      if (err) return res.status(500).send(err);
      res.status(201).json({ id: attraction._id });
    });
  });

  router.get('/attraction/:id', (req, res) => {
    Attraction.findById(req.params.id, (err, attraction) => {
      if (err) return res.status(500).send(err);
      if (!attraction) return res.status(404).send('Attraction not found');
      res.json(attraction);
    });
  });

  app.use(router);

  const server = http.createServer(app);
  const serverDomain = domain.create();
  serverDomain.on('error', (err) => {
    console.error('Server error:', err);
    server.close(() => {
      process.exit(1);
    });
  });
  serverDomain.run(() => {
    server.listen(process.env.PORT || 3000, () => {
      console.log(`Server running on port ${server.address().port}`);
    });
  });

  if (cluster.isWorker) {
    process.on('uncaughtException', (err) => {
      console.error('Uncaught exception in worker:', err);
      cluster.worker.disconnect();
    });
  }

  return server;
}

module.exports = startServer;

