const express = require('express');
const bodyParser = require('body-parser');
const cors = require('cors');
const PageView = require('./src/models/PageView');
const Prediction = require('./src/models/Prediction');

function setupExpressServer() {
  const app = express();
  app.use(cors());
  app.use(bodyParser.json());

  app.post('/track-page-view', async (req, res) => {
    const { pageId, userId } = req.body;
    try {
      const pageView = new PageView({ pageId, userId });
      await pageView.save();
      res.status(201).send('Page view tracked');
    } catch (error) {
      res.status(500).send('Error tracking page view');
    }
  });

  app.post('/predictions', async (req, res) => {
    const { userId, predictionData } = req.body;
    try {
      const prediction = new Prediction({ userId, ...predictionData });
      await prediction.save();
      res.status(201).send('Prediction stored');
    } catch (error) {
      res.status(500).send('Error storing prediction');
    }
  });

  app.get('/predictions', async (req, res) => {
    try {
      const predictions = await Prediction.find();
      res.json(predictions);
    } catch (error) {
      res.status(500).send('Error fetching predictions');
    }
  });

  app.get('/prefetch', async (req, res) => {
    const { userId } = req.query;
    try {
      const previousPageId = await getPreviousPageId(userId);
      const shouldPrefetch = await shouldPrefetch(userId, previousPageId);
      res.json({ shouldPrefetch });
    } catch (error) {
      res.status(500).send('Error determining prefetch');
    }
  });

  const PORT = process.env.PORT || 3000;
  app.listen(PORT, () => {
    console.log(`Server running on port ${PORT}`);
  });

  return app;
}

module.exports = setupExpressServer;

