const express = require('express');
const exphbs = require('express-handlebars');
const path = require('path');

function getServer(P) {
  const app = express();
  const port = Number(P || process.env.PORT || 8000);

  app.engine('hbs', exphbs({ extname: '.hbs' }));
  app.set('view engine', 'hbs');

  if (port === 8000) {
    app.set('views', path.join(__dirname, 'views/reference'));
  } else {
    app.set('views', path.join(__dirname, 'views/dev'));
  }

  app.use(express.static(path.join(__dirname, 'public')));

  app.get('/', (req, res) => {
    res.render('test', { config: { port } });
  });

  process.title = port === 8000 ? 'reference-server' : 'dev-server';

  const server = app.listen(port, () => {
    console.log(`Server running on port ${port}`);
  });

  return server;
}

module.exports = getServer;
