const express = require('express');
const connect = require('connect');
const fs = require('fs');
const nodemailer = require('nodemailer');
const lcrypto = require('lcrypto');
const lutil = require('lutil');
const auth = require('./auth');
const processInfo = require('./processInfo');
const locker = require('./locker');
const request = require('request');

function setupExpressServer() {
  const app = express();

  app.use(express.json());
  app.use(express.urlencoded({ extended: true }));
  app.use(connect.cookieParser());

  app.get('/auth', (req, res) => {
    res.send(auth);
  });

  app.post('/send-test-email', (req, res) => {
    const { to, subject, text } = req.body;
    const transporter = nodemailer.createTransport({
      host: auth.smtpHost,
      port: auth.smtpPort,
      secure: true,
      auth: {
        user: auth.smtpUser,
        pass: auth.smtpPass
      }
    });

    const mailOptions = {
      from: auth.smtpUser,
      to,
      subject,
      text
    };

    transporter.sendMail(mailOptions, (error, info) => {
      if (error) {
        return res.status(500).send(error.toString());
      }
      res.send('Test email sent: ' + info.response);
    });
  });

  app.post('/save-config', (req, res) => {
    const { smtpHost, smtpPort, smtpUser, smtpPass } = req.body;
    const encryptedPass = lcrypto.encrypt(smtpPass, auth.encryptionKey);
    const config = {
      smtpHost,
      smtpPort,
      smtpUser,
      smtpPass: encryptedPass
    };
    fs.writeFileSync('config.json', JSON.stringify(config));
    res.send('Configuration saved');
  });

  app.get('/get-config', (req, res) => {
    try {
      const data = fs.readFileSync('config.json', 'utf8');
      res.send(data);
    } catch (err) {
      res.status(404).send('No configuration found');
    }
  });

  app.get('/lockers', (req, res) => {
    request.get(`${locker.baseURL}/lockers`, (error, response, body) => {
      if (error) {
        return res.status(500).send(error.toString());
      }
      res.send(body);
    });
  });

  app.listen(processInfo.port, () => {
    console.log(`Email server running on port ${processInfo.port}`);
  });
}

