function setupExpressServer() {
  const express = require('express');
  const app = express();
  const port = 3000;

  // Basic middleware
  app.use((req, res, next) => {
    console.log('Basic middleware executed');
    next();
  });

  // Route handler with termination
  app.get('/terminate', (req, res) => {
    console.log('Terminating route handler');
    res.send('Request terminated here');
  });

  // Route handler without termination
  app.get('/continue', (req, res) => {
    console.log('Continuing route handler');
    res.send('Request continued');
  });

  // Error handling middleware
  app.use((err, req, res, next) => {
    console.error('Error occurred:', err.message);
    res.status(500).send('Internal Server Error');
  });

  // Unhandled routes
  app.use((req, res) => {
    console.log('Unhandled route:', req.url);
    res.status(404).send('Route not found');
  });

  app.listen(port, () => {
    console.log(`Server is running on http://localhost:${port}`);
  });
}

