const express = require('express');
const http = require('http');
const socketIO = require('socket.io');
const { ClusterManager, Particle } = require('./particleSimulation');

function startServer() {
  const app = express();
  const server = http.createServer(app);
  const io = socketIO(server);

  app.use(express.static('public'));

  const clusterManager = new ClusterManager();

  io.on('connection', (socket) => {
    console.log('A client connected');

    socket.on('joinCluster', (clusterId) => {
      const cluster = clusterManager.getCluster(clusterId);
      if (cluster) {
        socket.join(clusterId);
        socket.emit('clusterJoined', clusterId);
      }
    });

    socket.on('createParticle', (data) => {
      const { clusterId, x, y, radius, color } = data;
      const cluster = clusterManager.getCluster(clusterId);
      if (cluster) {
        const particle = new Particle(x, y, radius, color);
        cluster.addParticle(particle);
        io.to(clusterId).emit('newParticle', particle);
      }
    });

    socket.on('moveParticles', (data) => {
      const { clusterId, dx, dy } = data;
      const cluster = clusterManager.getCluster(clusterId);
      if (cluster) {
        cluster.particles.forEach(particle => {
          particle.move(dx, dy);
          cluster.checkCollisions(particle);
        });
        io.to(clusterId).emit('particlesMoved', cluster.particles);
      }
    });

    socket.on('disconnect', () => {
      console.log('A client disconnected');
    });
  });

  server.listen(3000, () => {
    console.log('Server is running on port 3000');
  });
}
