const express = require('express');
const cors = require('cors');
const axios = require('axios');

function setupExpressServer() {
  const app = express();
  const PORT = process.env.PORT || 3000;

  const corsOptions = {
    origin: 'http://localhost:5173',
    optionsSuccessStatus: 200
  };

  app.use(cors(corsOptions));
  app.use(express.json());

  app.get('/wikipedia_summaries', async (req, res) => {
    const { keyword } = req.query;

    if (!keyword) {
      return res.status(400).json({ error: 'Keyword is required' });
    }

    try {
      const searchResponse = await axios.get(`https://en.wikipedia.org/w/api.php`, {
        params: {
          action: 'query',
          list: 'search',
          srsearch: keyword,
          format: 'json'
        }
      });

      const pages = searchResponse.data.query.search;
      const summaries = [];

      for (const page of pages) {
        const summaryResponse = await axios.get(`https://en.wikipedia.org/w/api.php`, {
          params: {
            action: 'query',
            prop: 'extracts',
            titles: page.title,
            format: 'json',
            exintro: true,
            explaintext: true
          }
        });

        const extract = Object.values(summaryResponse.data.query.pages)[0].extract;
        const categoriesResponse = await axios.get(`https://en.wikipedia.org/w/api.php`, {
          params: {
            action: 'query',
            prop: 'categories',
            titles: page.title,
            format: 'json'
          }
        });

        const categories = Object.values(categoriesResponse.data.query.pages)[0].categories.map(cat => cat.title);
        summaries.push({ title: page.title, summary: extract, categories });
      }

      res.json(summaries);
    } catch (error) {
      console.error(error);
      res.status(500).json({ error: 'Failed to fetch Wikipedia summaries' });
    }
  });

  app.listen(PORT, () => {
    console.log(`Server is running on http://localhost:${PORT}`);
  });
