var fs = require('fs');
var http = require('http');

class Server {
  constructor(options) {
    this.options = options || {};
    this.options.livereload = this.options.livereload || path.join(__dirname, 'public/livereload.js');
    this.options.port = parseInt(this.options.port || 35729, 10);
    this.clients = {};
    this.configure(this.options.app);
  }

  configure(app) {
    if(!app) {
      this.server = http.createServer(this.handler.bind(this));
      this.server.on('error', this.error.bind(this));
      return this;
    }

    var self = this;
    this.app = app;

    this.app.listen = function(port, done) {
      done = done || function() {};
      var srv = self.server = http.createServer(app);
      srv.on('error', self.error.bind(self));
      srv.on('close', self.close.bind(self));
      return srv.listen(port, done);
    };

    return this;
  }

  handler(req, res, next) {
    var middleware = typeof next === 'function';
    if(middleware) {
      this.handle(req, res, next);
      return this;
    }

    req
      .on('end', this.handle.bind(this, req, res))
      .on('data', function(chunk) {
        req.data = req.data || '';
        req.data += chunk;
      });

    return this;
  }

  livereload(req, res) {
    fs.createReadStream(this.options.livereload).pipe(res);
  }

  listen(port, fn) {
    this.port = port;
    this.server.listen(port, fn);
  }

  close(req, res) {
    if(res) res.end();
    if(this.server._handle) this.server.close();
  }

  error(e) {
    console.error('Error:', e.message);
    console.error(e.stack);
  }
}

