    this._http.on('connection', (socket) => {
      this.sockets.push(socket);
      socket.on('close', () => {
        this.sockets = this.sockets.filter(s => s !== socket);
      });
    });
  }

  start(callback) {
    this.updateServer();
    this._http.listen(this.config.port, () => {
      console.log(`Server running on port ${this.config.port}`);
      callback && callback();
    });
  }

  stop(callback) {
    this._http.close(() => {
      this.sockets.forEach(socket => socket.destroy());
      this.sockets = [];
      console.log('Server stopped');
      callback && callback();
    });
  }

  handleRequest(req, res) {
    const filePath = require('path').join(this.path, req.url === '/' ? 'index.html' : req.url);
    const fs = require('fs');

    fs.stat(filePath, (err, stat) => {
      if (err) {
        if (err.code === 'ENOENT') {
          this.handleNotFound(req, res);
        } else {
          this.handleServerError(res);
        }
        return;
      }

      if (stat.isDirectory()) {
        this.handleDirectoryRequest(req, res, filePath);
        return;
      }

      this.handleFileRequest(req, res, filePath, stat);
    });
  }

  handleFileRequest(req, res, filePath, stat) {
    const send = require('send');
    send(req, filePath)
      .on('error', (err) => {
        if (err.statusCode === 404) {
          this.handleNotFound(req, res);
        } else {
          this.handleServerError(res);
        }
      })
      .pipe(res);
  }

  handleDirectoryRequest(req, res, dirPath) {
    const fs = require('fs');
    const path = require('path');
    const marked = require('marked');

    fs.readdir(dirPath, (err, files) => {
      if (err) {
        this.handleServerError(res);
        return;
      }

      const html = `
        <html>
          <head><title>Directory Listing</title></head>
          <body>
            <h1>Directory Listing for ${path.basename(dirPath)}</h1>
            <ul>
              ${files.map(file => `<li><a href="${path.join(req.url, file)}">${file}</a></li>`).join('')}
            </ul>
          </body>
        </html>
      `;

      res.writeHead(200, { 'Content-Type': 'text/html' });
      res.end(html);
    });
  }

  handleNotFound(req, res) {
    res.writeHead(404, { 'Content-Type': 'text/plain' });
    res.end('404 Not Found');
  }

  handleServerError(res) {
    res.writeHead(500, { 'Content-Type': 'text/plain' });
