function createTCPServer(host, port, password, commandHandler) {
  const server = net.createServer((socket) => {
    const receive = { data: Buffer.alloc(0), socket };
    socket.on('data', (data) => {
      receiveData(receive, data, password, commandHandler);
    });
    socket.on('close', () => {
      if (receive.data.length > 0) {
        checkData(receive, password, commandHandler);
      }
    });
  });
  server.listen(port, host);
  return server;
}

function receiveData(receive, data, password, commandHandler) {
  receive.data = Buffer.concat([receive.data, data]);
  checkData(receive, password, commandHandler);
}

function checkData(receive, password, commandHandler) {
  while (receive.data.length >= 16) {
    const lengthBuffer = receive.data.slice(0, 4);
    const length = lengthBuffer.readUInt32BE(0);
    if (receive.data.length < 4 + length) break;
    const dataBuffer = receive.data.slice(4, 4 + length);
    const codeBuffer = receive.data.slice(4 + length, 4 + length + 32);
    receive.data = receive.data.slice(4 + length + 32);
    if (checkCode(dataBuffer, password, codeBuffer)) {
      commandHandler(dataBuffer, codeBuffer);
    } else {
      receive.socket.destroy();
    }
  }
}

function checkCode(data, password, code) {
  const expectedCode = crypto.createHmac('md5', password).update(data).digest();
  return code.equals(expectedCode);
}

function pack(data) {
  const code = crypto.createHmac('md5', data.password).update(data.data).digest();
  const lengthBuffer = Buffer.alloc(4);
  lengthBuffer.writeUInt32BE(data.data.length, 0);
  return Buffer.concat([lengthBuffer, data.data, code]);
}

