    this.host = host;
    this.port = port;
    this.family = 'ipv4';

    // Create a server socket
    this.server = options.tls ? tls.createServer(options) : net.createServer(options);
    this.tlsEnabled = !!options.tls;

    // state
    this.state = 'stopped';

    // Number of connections
