class MockServer {
  constructor(port, host, options) {
    this.host = host;
    this.port = port;
    this.family = options?.family || 'ipv4';
    this.tlsEnabled = options?.tlsEnabled || false;
    this.state = 'stopped';
    this.connections = 0;
    this.sockets = [];
    this.server = null;
  }

  start() {
    return new Promise((resolve, reject) => {
      if (this.state !== 'stopped') {
        reject(new Error('Server is already running or destroyed'));
        return;
      }

      this.state = 'running';

      const serverOptions = {
        host: this.host,
        port: this.port,
        family: this.family
      };

      if (this.tlsEnabled) {
        const tls = require('tls');
        this.server = tls.createServer({
          key: require('fs').readFileSync(options.key),
          cert: require('fs').readFileSync(options.cert)
        }, (socket) => {
          this.handleConnection(socket);
        });
      } else {
        const net = require('net');
        this.server = net.createServer((socket) => {
          this.handleConnection(socket);
        });
      }

      this.server.on('error', (err) => {
        this.state = 'stopped';
        reject(err);
      });

      this.server.on('listening', () => {
        resolve();
      });

      this.server.listen(serverOptions);
    });
  }

  handleConnection(socket) {
    this.connections++;
    this.sockets.push(socket);

    socket.on('data', (data) => {
      // Handle incoming data
    });

    socket.on('close', () => {
      this.connections--;
      this.sockets = this.sockets.filter(s => s !== socket);
    });

    socket.on('error', (err) => {
      console.error('Socket error:', err);
      this.connections--;
      this.sockets = this.sockets.filter(s => s !== socket);
    });
  }

  destroy() {
    return new Promise((resolve, reject) => {
      if (this.state === 'destroyed') {
        resolve();
        return;
      }

      if (this.state !== 'running') {
        reject(new Error('Server is not running'));
        return;
      }

      this.state = 'destroyed';

      if (this.server) {
        this.server.close((err) => {
          if (err) {
            reject(err);
          } else {
            this.server = null;
            this.connections = 0;
            this.sockets = [];
            resolve();
          }
        });
      } else {
        resolve();
      }
    });
  }
