class TcpReader extends require('events').EventEmitter {
  constructor(transporter, opts) {
    super();
    this.server = require('net').createServer();
    this.opts = opts;
    this.transporter = transporter;
    this.Promise = transporter.Promise;
    this.logger = transporter.logger;
    this.sockets = [];
  }

  listen() {
    return new this.Promise((resolve, reject) => {
      this.server.on('error', (err) => {
        this.logger.error(`TCP server error: ${err.message}`);
        reject(err);
      });

      this.server.listen(this.opts.port, () => {
        this.logger.info(`TCP server listening on port ${this.opts.port}`);
        resolve(this.server.address().port);
      });

      this.server.on('connection', (socket) => {
        this.onTcpClientConnected(socket);
      });

      this.server.on('close', () => {
        this.logger.info('TCP server closed');
        this.emit('close');
      });
    });
  }

  onTcpClientConnected(socket) {
    this.logger.info(`New client connected: ${socket.remoteAddress}:${socket.remotePort}`);
    this.sockets.push(socket);

    socket.on('data', (data) => {
      try {
        const parsedData = this.parseData(data);
        this.emit('data', parsedData, socket);
      } catch (err) {
        this.logger.error(`Error parsing data from ${socket.remoteAddress}:${socket.remotePort}: ${err.message}`);
        this.closeSocket(socket);
      }
    });

    socket.on('error', (err) => {
      this.logger.error(`Client connection error: ${err.message}`);
      this.closeSocket(socket);
    });

    socket.on('close', () => {
      this.logger.info(`Client disconnected: ${socket.remoteAddress}:${socket.remotePort}`);
      this.closeSocket(socket);
    });
  }

  parseData(data) {
    if (data.length > this.opts.maxPacketSize) {
      throw new Error(`Packet size exceeds maximum allowed: ${this.opts.maxPacketSize} bytes`);
    }
    return data.toString();
  }

  closeSocket(socket) {
    const index = this.sockets.indexOf(socket);
    if (index !== -1) {
      this.sockets.splice(index, 1);
      socket.destroy();
    }
  }

  close() {
    this.logger.info('Closing TCP server...');
    this.server.close(() => {
      this.sockets.forEach((socket) => {
        this.closeSocket(socket);
      });
      this.emit('closed');
    });
  }
}

