    this.tcpServer = require('net').createServer((socket) => {
      this.emit('connection', socket);
    });

    this.tcpServer.on('error', (err) => {
      this.emit('error', err);
    });

    this.tcpServer.on('close', () => {
      this.emit('close');
    });

    this.tcpServer.listen(this.port, this.host, () => {
      this.emit('listening');
      if (cb) cb();
    });
  }

  stop(force, cb) {
    if (!this.tcpServer) {
      if (cb) cb();
      return;
    }

    this.tcpServer.close(() => {
      this.tcpServer = null;
      this.emit('closed');
      if (cb) cb();
    });

    if (force) {
      this.tcpServer.getConnections((err, count) => {
        if (err) return;
        for (let i = 0; i < count; i++) {
          this.tcpServer.close();
        }
      });
    }
  }
}

