class DNodeServer {
  constructor(cons, opts) {
    this.cons = cons;
    this.opts = opts || {};
    this.server = require('net').createServer();
    this.server.sessions = {};
    if (this.opts.weak) {
      this._handleConnection = this._handleConnection.bind(this);
    }
  }

  _handleConnection(stream) {
    const sessionId = this._randomId();
    this.server.sessions[sessionId] = {
      stream,
      callbacks: new Map()
    };
    stream.on('data', (data) => {
      const message = JSON.parse(data.toString());
      if (message.type === 'event') {
        const callback = this.server.sessions[sessionId].callbacks.get(message.id);
        if (callback) {
          if (this.opts.weak) {
            callback(message.data);
          } else {
            callback(message.data);
            this.server.sessions[sessionId].callbacks.delete(message.id);
          }
        }
      }
    });
    stream.on('close', () => {
      delete this.server.sessions[sessionId];
    });
    this.cons(stream, sessionId);
  }

  listen(port, host, callback) {
    this.server.on('connection', this._handleConnection);
    return this.server.listen(port, host, callback);
  }

  _randomId() {
    return Math.random().toString(16).substring(2, 18);
  }
}

