class TcpReader {
  constructor(transporter, opts) {
    this.transporter = transporter;
    this.opts = opts;
    this.sockets = [];
    this.logger = transporter.logger;
    this.server = require('net').createServer();
    this.connected = false;
  }

  listen() {
    return new Promise((resolve, reject) => {
      this.server.on('error', (err) => {
        this.logger.error(`TCP server error: ${err.message}`);
        reject(err);
      });

      this.server.on('connection', (socket) => {
        this.onTcpClientConnected(socket);
      });

      this.server.listen(this.opts.port, () => {
        this.connected = true;
        this.logger.info(`TCP server listening on port ${this.opts.port}`);
        resolve();
      });
    });
  }

  onTcpClientConnected(socket) {
    this.sockets.push(socket);

    socket.on('data', (data) => {
      try {
        this.transporter.handleMessage(data.toString());
      } catch (err) {
        this.logger.error(`Error processing message: ${err.message}`);
        this.closeSocket(socket, err);
      }
    });

    socket.on('close', () => {
      this.closeSocket(socket);
    });

    socket.on('error', (err) => {
      this.logger.error(`Socket error: ${err.message}`);
      this.closeSocket(socket, err);
    });
  }

  closeSocket(socket, err) {
    const index = this.sockets.indexOf(socket);
    if (index !== -1) {
      this.sockets.splice(index, 1);
    }

    if (socket.destroyed) return;

    socket.removeAllListeners();
    socket.destroy(err);
  }

  close() {
    if (!this.connected) return;

    this.connected = false;
    this.sockets.forEach((socket) => this.closeSocket(socket));
    this.server.close((err) => {
      if (err) this.logger.error(`Error closing TCP server: ${err.message}`);
    });
  }
}

