class RedisInterceptor {
  constructor(Pythagora, listenPort, targetPort, intermediateData) {
    this.Pythagora = Pythagora;
    this.listenPort = listenPort;
    this.targetPort = targetPort || 6379;
    this.intermediateData = intermediateData || [];
    this.listenSocket = null;
    this.targetSocket = null;
    this.mode = undefined;
  }

  async init() {
    this.listenSocket = require('net').createServer((connection) => {
      this.handleClientConnection(connection);
    });
    return new Promise((resolve, reject) => {
      this.listenSocket.on('error', reject);
      this.listenSocket.listen(this.listenPort, () => {
        resolve();
      });
    });
  }

  handleClientConnection(connection) {
    this.targetSocket = require('net').createConnection(this.targetPort, '127.0.0.1', () => {
      connection.pipe(this.targetSocket);
      this.targetSocket.pipe(connection);
    });

    connection.on('data', (data) => {
      this.forwardData(connection, data, true);
    });

    connection.on('close', () => {
      if (this.targetSocket) {
        this.targetSocket.destroy();
        this.targetSocket = null;
      }
    });

    connection.on('error', (err) => {
      console.error('Client connection error:', err);
    });
  }

  forwardData(connection, data, saveData) {
    if (this.mode === MODES.CAPTURE && saveData) {
      this.Pythagora.saveData(data);
    } else if (this.mode === MODES.TEST) {
      const mockResponse = this.getMockResponse(data);
      if (mockResponse) {
        connection.write(mockResponse);
        return;
      }
    }

    this.targetSocket.write(data);
  }

  getMockResponse(request) {
    for (const [req, res] of this.intermediateData) {
      if (request.toString() === req) {
        return res;
      }
    }
    return null;
  }

  splitIntoChunks(str, chunkSize) {
    const chunks = [];
    for (let i = 0; i < str.length; i += chunkSize) {
      chunks.push(str.slice(i, i + chunkSize));
    }
    return chunks;
  }
}

