class Node {
  constructor(id) {
    this.id_ = id;
    this.peers_ = {};
    this.PORT = 3000;
    this.server_ = require('net').createServer();
    this.server_.on('connection', (socket) => this.on_data.bind(this)(null, socket));
  }

  start() {
    return new Promise((resolve) => {
      this.server_.listen(this.PORT + this.id_, () => {
        console.log(`Node ${this.id_} started on port ${this.PORT + this.id_}`);
        resolve();
      });
    });
  }

  on_data(data, socket) {
    if (data) {
      const message = JSON.parse(data.toString());
      if (message.type === 'peer') {
        this.add_peer(socket, message.id);
      } else {
        this.emit('message', message);
      }
    }
  }

  send(socket, data) {
    if (typeof socket === 'number') {
      const peerSocket = this.peers_[socket];
      if (peerSocket) {
        peerSocket.write(JSON.stringify(data));
      }
    } else {
      socket.write(JSON.stringify(data));
    }
  }

  broadcast(data) {
    for (const peerId in this.peers_) {
      this.send(parseInt(peerId), data);
    }
  }

  add_peer(socket, remote_id) {
    this.peers_[remote_id] = socket;
    socket.on('data', (data) => this.on_data.bind(this)(data, socket));
    socket.on('close', () => this.remove_peer(socket));
    this.emit('peer_connected', remote_id);
  }

  remove_peer(socket) {
    for (const peerId in this.peers_) {
      if (this.peers_[peerId] === socket) {
        delete this.peers_[peerId];
        this.emit('peer_disconnected', peerId);
        break;
      }
    }
  }

  list_peers() {
    return Object.keys(this.peers_);
  }

  emit(event, data) {
    if (this[event]) {
      this[event](data);
    }
  }
}

