const { v4: uuidv4 } = require('uuid');

function setupUDPServer(argv, io, debug) {
  const udpPort = argv.udpPort;
  const udpHost = argv.udpHost || '0.0.0.0';
  const streams = {};

  const server = dgram.createSocket('udp4');

  server.on('message', (msg, rinfo) => {
    try {
      const data = JSON.parse(msg);
      const streamId = data.streamId || uuidv4();
      const timestamp = Date.now();

      if (!streams[streamId]) {
        streams[streamId] = {
          messages: [],
          lastUpdated: timestamp
        };
      }

      const stream = streams[streamId];
      stream.messages.push({ ...data, timestamp });
      if (stream.messages.length > 100) {
        stream.messages.shift();
      }
      stream.lastUpdated = timestamp;

      io.to('streams').emit('streamUpdate', Object.keys(streams));
      io.to(streamId).emit('message', data);
    } catch (err) {
      debug(`Error processing UDP message: ${err.message}`);
    }
  });

  server.on('listening', () => {
    debug(`UDP server listening on ${udpHost}:${udpPort}`);
  });

  server.bind(udpPort, udpHost);
