    const pingMessage = Buffer.from('PING');
    this.udpServer.send(pingMessage, this.targetPort, this.targetHost, (err) => {
      if (err) {
        this.callback(err);
        return;
      }
      this.udpServer.once('message', this.onResponseCallback.bind(this));
      setTimeout(() => {
        this.timeoutReached();
      }, this.timeout);
    });
