  onResponseCallback(message, sender) {
    if (message.toString() === 'PONG') {
      this.callback(null, sender);
    } else {
      this.callback(new Error('Invalid response received'));
    }
  }

  timeoutReached() {
    this.udpServer.removeAllListeners('message');
    this.callback(new Error('Timeout reached'));
  }
