class MasterIPC {
  constructor(options) {
    this.options = options;
    this._sock = null;
    this.children = [];
    this.sock = null;
  }

  IPCSocket() {
    if (!this._sock) {
      this._sock = require('dgram').createSocket('udp4');
      this._sock.on('message', (msg, rinfo) => {
        const data = JSON.parse(msg);
        if (data.method && this[data.method]) {
          this[data.method](...data.args, data.worker);
        }
      });
    }
    return this._sock;
  }

  setupIPC() {
    this.sock = this.IPCSocket();
    this.sock.bind(this.options.port, this.options.address, () => {
      this.sock.emit('start');
    });
  }

  call(id, method, ...args) {
    const message = JSON.stringify({ id, method, args });
    this.sock.send(message, 0, message.length, this.options.port, this.options.address);
  }

  invoke(method, args, worker) {
    if (this[method]) {
      this[method](...args, worker);
    }
  }

  connect(worker) {
    this.children.push(worker);
    this.sock.emit('worker connected', worker);
  }

  connectMaster(sig) {
    if (this.sock && this.sock.listening) {
      process.kill(process.parentPID, sig);
    } else {
      this.sock.on('listening', () => {
        process.kill(process.parentPID, sig);
      });
    }
  }

  masterKilled(data) {
    this.sock.emit('restart', data);
  }
}

