  server.on('connection', (socket) => {
    socket.on('message', (message) => {
      if (message.length === 0) {
        socket.send(message);
      }
    });
  });

  const client = new WebSocket('ws://localhost:8080');

  client.on('open', () => {
    client.send(Buffer.alloc(0));
  });

