class ReconnectingWebSocket {
  constructor(url, protocols = [], options = {}) {
    this.debug = options.debug || false;
    this.automaticOpen = options.automaticOpen !== false;
    this.reconnectInterval = options.reconnectInterval || 1000;
    this.maxReconnectInterval = options.maxReconnectInterval || 30000;
    this.reconnectDecay = options.reconnectDecay || 2;
    this.timeoutInterval = options.timeoutInterval || 5000;
    this.url = url;
    this.reconnectAttempts = 0;
    this.readyState = WebSocket.CLOSED;
    this.protocol = null;
    this._events = {};
    this._ws = null;
    this._timeout = null;
    this._reconnectTimeout = null;

    if (protocols.length > 0) {
      this.protocol = protocols[0];
    }

    if (this.automaticOpen) {
      this.open(false);
    }
  }

  addEventListener(type, listener) {
    if (!this._events[type]) {
      this._events[type] = [];
    }
    this._events[type].push(listener);
  }

  removeEventListener(type, listener) {
    if (this._events[type]) {
      this._events[type] = this._events[type].filter(l => l !== listener);
    }
  }

  dispatchEvent(event) {
    const listeners = this._events[event.type];
    if (listeners) {
      listeners.forEach(listener => listener(event));
    }
  }

  open(wasReconnect) {
    if (this._ws) {
      this._ws.close();
      this._ws = null;
    }

    this._ws = new WebSocket(this.url, this.protocol);
    this.readyState = WebSocket.CONNECTING;

    this._ws.onopen = (event) => {
      this.readyState = WebSocket.OPEN;
      this.reconnectAttempts = 0;
      this._clearTimeouts();
      this.dispatchEvent(new CustomEvent('open', { detail: event }));
      if (wasReconnect) {
        this.dispatchEvent(new CustomEvent('reconnect', { detail: event }));
      }
    };

    this._ws.onmessage = (event) => {
      this.dispatchEvent(new CustomEvent('message', { detail: event }));
    };

    this._ws.onclose = (event) => {
      this.readyState = WebSocket.CLOSED;
      this.dispatchEvent(new CustomEvent('close', { detail: event }));
      this._handleClose(event);
    };

    this._ws.onerror = (event) => {
      this.dispatchEvent(new CustomEvent('error', { detail: event }));
    };

