function manageTests() {
  const socket = new WebSocket('ws://test-server.com');
  const maxParallelRuns = 5;
  const subscriptionKey = Math.random();
  let queue = [];
  let numRunning = 0;

  function processQueue() {
    while (numRunning < maxParallelRuns && queue.length > 0) {
      const test = queue.shift();
      const window = window.open(test.url, '_blank');
      if (window) {
        window.addEventListener('message', function(event) {
          if (event.data && event.data.type === 'testResult') {
            socket.send(JSON.stringify({
              type: 'testResult',
              subscriptionKey: subscriptionKey,
              testId: test.id,
              result: event.data.result
            }));
          }
        });
        numRunning++;
      }
    }
  }

  socket.onopen = function() {
    socket.send(JSON.stringify({
      type: 'subscribe',
      subscriptionKey: subscriptionKey
    }));
  };

  socket.onmessage = function(event) {
    const message = JSON.parse(event.data);
    if (message.type === 'enqueueTest') {
      queue.push(message.test);
      processQueue();
    } else if (message.type === 'testStatus') {
      const statusElement = document.getElementById(`status-${message.testId}`);
      if (statusElement) {
        statusElement.textContent = message.status;
      }
    } else if (message.type === 'testResult') {
      const resultElement = document.getElementById(`result-${message.testId}`);
      if (resultElement) {
        resultElement.textContent = message.result;
      }
      numRunning--;
      processQueue();
    }
  };
}

