class RPCWebSocket {
  constructor(address, encryption, namespace) {
    this._namespace = namespace || '';
    this._listeners = {};
    this._handles = {};
    this._socket = null;
    this.setAddress(address, encryption);
  }

  setAddress(address, encryption) {
    const protocol = encryption ? 'wss' : 'ws';
    this._socket = new WebSocket(`${protocol}://${address}`);
    this._socket.onopen = () => this._onopen();
    this._socket.onmessage = (event) => this._onmessage(event);
    this._socket.onclose = () => this._onclose();
    this._socket.onerror = (error) => this._onerror(error);
  }

  addListener(method, callback) {
    this._listeners[method] = this._listeners[method] || [];
    this._listeners[method].push(callback);
  }

  removeListener(method) {
    delete this._listeners[method];
  }

  request(method, params, id, successCallback, errorCallback) {
    const data = this._formatData(method, params, id);
    this._send(data);
    this._handles[id] = { success: successCallback, error: errorCallback };
  }

  batchRequest(requests, successCallback, errorCallback) {
    const batch = requests.map(req => this._formatData(req.method, req.params, req.id));
    this._send(batch);
    requests.forEach(req => {
      this._handles[req.id] = { success: successCallback, error: errorCallback };
    });
  }

  _formatData(method, params, id) {
    return {
      jsonrpc: '2.0',
      method: `${this._namespace}${method}`,
      params: params || {},
      id: id || Math.random().toString(36).substr(2, 9)
    };
  }

  _send(data) {
    if (this._socket && this._socket.readyState === WebSocket.OPEN) {
      this._socket.send(JSON.stringify(data));
    } else {
      setTimeout(() => this._send(data), 1000);
    }
  }

  _onerror(error) {
    Object.values(this._handles).forEach(handle => handle.error && handle.error(error));
    this._handles = {};
  }

  _onmessage(event) {
    const message = JSON.parse(event.data);
    if (message.jsonrpc === '2.0' && message.id) {
      const handle = this._handles[message.id];
      if (handle && message.result) {
        handle.success && handle.success(message.result);
        delete this._handles[message.id];
      } else if (handle && message.error) {
        handle.error && handle.error(message.error);
        delete this._handles[message.id];
      }
    } else if (message.jsonrpc === '2.0' && message.method) {
      const listeners = this._listeners[message.method];
      if (listeners) {
        listeners.forEach(listener => listener(message.params));
      }
    }
  }

  _onopen() {
    // Connection established
  }

  _onclose() {
    // Connection closed, attempt to reconnect
    setTimeout(() => this.setAddress(this._socket.url.split('://')[1], this._socket.url.startsWith('wss')), 5000);
  }
}

