class ReconnectingWebSocket {
  constructor(url, protocols = [], options = {}) {
    this.url = url;
    this.protocols = protocols;
    this.debug = options.debug || false;
    this.automaticOpen = options.automaticOpen !== false;
    this.reconnectInterval = options.reconnectInterval || 1000;
    this.maxReconnectInterval = options.maxReconnectInterval || 30000;
    this.reconnectDecay = options.reconnectDecay || 1.5;
    this.timeoutInterval = options.timeoutInterval || 2000;
    this.reconnectAttempts = 0;
    this.readyState = WebSocket.CLOSED;
    this.protocol = '';
    this._events = {};
    this._socket = null;
    this._timeout = null;
    this._reconnectTimeout = null;

    if (this.automaticOpen) {
      this.open();
    }
  }

  addEventListener(type, listener) {
    if (!this._events[type]) {
      this._events[type] = [];
    }
    this._events[type].push(listener);
  }

  removeEventListener(type, listener) {
    if (this._events[type]) {
      this._events[type] = this._events[type].filter(l => l !== listener);
    }
  }

  dispatchEvent(event) {
    if (this._events[event.type]) {
      this._events[event.type].forEach(listener => listener(event));
    }
  }

  open(reconnecting = false) {
    if (this.readyState === WebSocket.OPEN) {
      return;
    }

    this.readyState = WebSocket.CONNECTING;
    this._reconnectAttempts = 0;
    this._socket = new WebSocket(this.url, this.protocols);

    this._socket.onopen = () => {
      this.readyState = WebSocket.OPEN;
      this.protocol = this._socket.protocol;
      this.dispatchEvent(new Event('open'));
      this.onopen();
    };

    this._socket.onmessage = (event) => {
      this.dispatchEvent(new Event('message'));
      this.onmessage(event);
    };

    this._socket.onclose = (event) => {
      this.readyState = WebSocket.CLOSED;
      this.dispatchEvent(new Event('close'));
      this.onclose(event);

      if (!reconnecting) {
        this._startReconnect();
      }
    };

    this._socket.onerror = (event) => {
      this.dispatchEvent(new Event('error'));
      this.onerror(event);
    };

    this._timeout = setTimeout(() => {
      if (this.readyState === WebSocket.CONNECTING) {
        this._socket.close();
        this._startReconnect();
      }
    }, this.timeoutInterval);
  }

  send(data) {
    if (this.readyState === WebSocket.OPEN) {
      this._socket.send(data);
    } else {
      throw new Error('WebSocket is not open.');
    }
  }

  close(code, reason) {
    if (this._socket) {
      this._socket.close(code, reason);
      this._socket = null;
      this.readyState = WebSocket.CLOSED;
    }
  }

  refresh() {
    this.close();
    this.open(true);
  }

  onopen() {}

  onclose(event) {}

  onconnecting() {}

  onmessage(event) {}

  onerror(event) {}

  _startReconnect() {
    if (this.readyState === WebSocket.CLOSED) {
      this.dispatchEvent(new Event('connecting'));
      this.onconnecting();

      const delay = Math.min(
        this.reconnectInterval * Math.pow(this.reconnectDecay, this.reconnectAttempts),
        this.maxReconnectInterval
      );

      this._reconnectTimeout = setTimeout(() => {
        this.reconnectAttempts++;
        this.open(true);
      }, delay);
    }
  }
}

