      const data = this.JSON.parse(message.data);
      if (data.id) {
        const handle = this.handles.find(h => h.id === data.id);
        if (handle) {
          if (data.result) {
            handle.success(data.result);
          } else if (data.error) {
            handle.error(new Error(data.error.message));
          }
          this.handles = this.handles.filter(h => h.id !== data.id);
        }
      }
    } catch (e) {
      this.alerts.error('Failed to parse message: ' + e.message);
    }
  }

  invoke(opts) {
    const id = this.utils.uuid();
    const request = {
      jsonrpc: '2.0',
      method: opts.name,
      params: opts.params,
      id: id
    };
    this.sock.send(this.JSON.stringify(request));
    this.handles.push({
      id: id,
      success: opts.success,
      error: opts.error
    });
  }

