    } else if (message.type === 'transcription') {
      browser.runtime.sendMessage({ type: 'transcription_result', data: message });
    }
  };

  ws.onerror = (error) => {
    console.error('WebSocket error:', error);
    browser.runtime.sendMessage({ type: 'error', data: error });
  };

  ws.onclose = () => {
    isServerReady = false;
    isCapturing = false;
    browser.runtime.sendMessage({ type: 'disconnected' });
    if (mediaStream) {
      mediaStream.getTracks().forEach(track => track.stop());
    }
  };

  browser.runtime.onMessage.addListener((request, sender, sendResponse) => {
    if (request.type === 'start_recording') {
      isCapturing = true;
      ws.send(JSON.stringify({ type: 'config', data: { language, task, modelSize, useVad } }));
    } else if (request.type === 'stop_recording') {
      isCapturing = false;
    }
  });
};

