    this.webSockets[socketId] = socket;

    return new Promise((resolve, reject) => {
      socket.onopen = () => {
        socket.send(JSON.stringify({ type: 1, data: 'handshake' }));
        resolve(socketId);
      };

      socket.onerror = (event) => {
        reject(new Error(`WebSocket error: ${event.message}`));
      };
    });
  }

  async ping(socketId: string): Promise<void> {
    const socket = this.webSockets[socketId];
    if (!socket) {
      throw new Error(`Socket ${socketId} not found`);
    }

    return new Promise((resolve, reject) => {
      socket.send(JSON.stringify({ type: 0, data: 'ping' }));
      resolve();
    });
  }

  send(socketId: string, msg: object, onMessage: (data: any) => void): Promise<any> {
    const socket = this.webSockets[socketId];
    if (!socket) {
      return Promise.reject(new Error(`Socket ${socketId} not found`));
    }

    return new Promise((resolve, reject) => {
      socket.onmessage = (event) => {
        const data = JSON.parse(event.data);
        if (data.type === 2) {
          onMessage(data);
          resolve(data);
        }
      };

      socket.onerror = (event) => {
        reject(new Error(`WebSocket error: ${event.message}`));
      };

      socket.send(JSON.stringify(msg));
    });
  }

  close(socketId: string): Promise<void> {
    const socket = this.webSockets[socketId];
    if (!socket) {
      return Promise.resolve();
    }

    return new Promise((resolve) => {
      socket.close();
      delete this.webSockets[socketId];
      resolve();
    });
  }
}

