        const id = Math.round(Date.now() * 1000 + Math.random() * 1000).toString(16)
        const reqjson = {
            id,
            action: 'api',
            data: {
                url,
                method,
            },
        }
        const resp = new Promise((resolve) => {
            this.ev.on(id, resolve)
        })
        this.ws.send(JSON.stringify(reqjson))
        return resp as Promise<{
            status: number
            body: any
        }>
    }
