class CocogoatWebControl {
  port: number;
  token: string;
  hwnd: number;
  version: string;
  ev: Mitt;
  client: FlyType;
  ws: WebSocket | undefined;

  constructor(_port?: number) {
    this.port = _port || 32333;
    this.token = '';
    this.hwnd = 0;
    this.version = '';
    this.ev = new Mitt();
    this.client = new FlyType();
  }

  async authorize(): Promise<boolean> {
    return new Promise((resolve, reject) => {
      const ws = new WebSocket(`ws://localhost:${this.port}`);
      this.ws = ws;

      ws.onopen = () => {
        ws.send(JSON.stringify({ type: 'authorize', token: this.token }));
      };

      ws.onmessage = (event) => {
        const data = JSON.parse(event.data);
        if (data.type === 'authorized') {
          this.token = data.token;
          resolve(true);
        } else {
          resolve(false);
        }
      };

      ws.onerror = (error) => {
        reject(error);
      };

      ws.onclose = () => {
        resolve(false);
      };
    });
  }

  async wsInvoke(method: string, path: string, querystring?: Record<string, any>): Promise<{ status: number; body: any }> {
    return new Promise((resolve, reject) => {
      if (!this.ws || this.ws.readyState !== WebSocket.OPEN) {
        reject(new Error('WebSocket not connected'));
        return;
      }

      const payload = {
        method,
        path,
        querystring,
        token: this.token
      };

      this.ws.send(JSON.stringify(payload));

      const handler = (event: MessageEvent) => {
        const data = JSON.parse(event.data);
        if (data.type === 'response') {
          this.ev.off('message', handler);
          resolve(data);
        }
      };

      this.ev.on('message', handler);
    });
  }
}

