const WebSocket = require('ws');

class SnapdropServer {
  constructor(port) {
    this._wss = new WebSocket.Server({ port });
    this._rooms = {};

    this._wss.on('connection', (peer) => this._onConnection(peer));
    this._wss.on('headers', (headers, response) => this._onHeaders(headers, response));
  }

  _onConnection(peer) {
    peer.isAlive = true;

    peer.on('message', (message) => this._onMessage(peer, message));

    peer.on('close', () => this._leaveRoom(peer));

    peer.on('pong', () => {
      peer.isAlive = true;
    });

    this._joinRoom(peer);
  }

  _onHeaders(headers, response) {
    // Process headers and set cookies if needed
  }

  _onMessage(sender, message) {
    const data = JSON.parse(message);

    if (data.type === 'offer' || data.type === 'answer' || data.type === 'candidate') {
      const targetPeer = this._getPeerByRoom(data.room);
      if (targetPeer) {
        this._send(targetPeer, data);
      }
    }
  }

  _send(peer, message) {
    peer.send(JSON.stringify(message));
  }

  _joinRoom(peer) {
    const room = this._getRoomByIP(peer._socket.remoteAddress);
    if (!this._rooms[room]) {
      this._rooms[room] = [];
    }
    this._rooms[room].push(peer);
  }

  _leaveRoom(peer) {
    const room = this._getRoomByIP(peer._socket.remoteAddress);
    if (this._rooms[room]) {
      this._rooms[room] = this._rooms[room].filter(p => p !== peer);
    }
  }

  _keepAlive(peer) {
    setInterval(() => {
      if (!peer.isAlive) {
        peer.terminate();
        return;
      }
      peer.isAlive = false;
      peer.ping();
    }, 30000);
  }

  _getRoomByIP(ip) {
    return ip;
  }

  _getPeerByRoom(room) {
    if (this._rooms[room] && this._rooms[room].length > 0) {
      return this._rooms[room][0];
    }
    return null;
  }
}

module.exports = SnapdropServer;
