async function fetchSubscribedChannels(lang = 'id') {
  const puppeteer = require('puppeteer');
  const axios = require('axios');
  const { parse } = require('node-html-parser');

  let browser;
  try {
    browser = await puppeteer.launch({ headless: true });
    const page = await browser.newPage();
    await page.goto('https://streaming-service.com/config', { waitUntil: 'networkidle2' });

    const configHTML = await page.content();
    const configRoot = parse(configHTML);
    const credentialsScript = configRoot.querySelector('script[type="application/json"]');
    const configData = JSON.parse(credentialsScript.text);

    const authResponse = await axios.post('https://streaming-service.com/auth', {
      username: configData.username,
      password: configData.password
    });

    const token = authResponse.data.token;

    const channelsResponse = await axios.get('https://streaming-service.com/channels', {
      headers: { Authorization: `Bearer ${token}` }
    });

    const channels = channelsResponse.data.channels;

    const processedChannels = [];
    const seen = new Set();

    for (const channel of channels) {
      const key = `${channel.site_id}-${lang}`;
      if (!seen.has(key)) {
        processedChannels.push({
          lang,
          site_id: channel.site_id,
          name: channel.names[lang] || channel.names.id
        });
        seen.add(key);
      }
    }

    return processedChannels;
  } catch (error) {
    throw new Error(`Failed to fetch and process subscribed channels: ${error.message}`);
  } finally {
    if (browser) {
      await browser.close();
    }
  }
}

