class WebviewServer {
  constructor(dataSource, config) {
    this.server = require('http').createServer();
    this.secret = config.secret;
    this.connections = new Set();
    this.config = config;
    this.setupWebSocketServer(dataSource);
  }

  setupWebSocketServer(dataSource) {
    const WebSocket = require('ws');
    const wss = new WebSocket.Server({ server: this.server });

    wss.on('connection', (ws, request) => {
      const connection = new WebviewConnection(ws, dataSource, this.secret);
      this.connections.add(connection);
      ws.on('close', () => this.connections.delete(connection));
    });

    this.server.listen(this.config.port, () => {
      console.log(`WebSocket server is running on port ${this.config.port}`);
    });
  }

  get port() {
    return this.server.address().port;
  }
}

class WebviewConnection {
  constructor(ws, dataSource, secret) {
    this.ws = ws;
    this.dataSource = dataSource;
    this.secret = secret;
    this.authenticate();
    this.handleMessages();
  }

  authenticate() {
    this.ws.send(JSON.stringify({ type: 'auth', secret: this.secret }));
  }

  handleMessages() {
    this.ws.on('message', (message) => {
      const data = JSON.parse(message);
      if (data.type === 'request') {
        this.dataSource.fetchData(data.query).then((result) => {
          this.ws.send(JSON.stringify({ type: 'response', data: result }));
        });
      }
    });
  }
}

