function downloadIcons(url, style, callback) {
  const axios = require('axios');
  const fs = require('fs');
  const path = require('path');
  const { promisify } = require('util');
  const mkdirp = promisify(require('mkdirp'));
  const writeFile = promisify(fs.writeFile);
  const readFile = promisify(fs.readFile);
  const exists = promisify(fs.exists);

  const targetImagePath = path.join(__dirname, 'icons');

  let processedIcons = [];
  let duplicateCount = 0;

  async function processIcon(iconData, index) {
    try {
      const name = iconData.name;
      const svgUrl = iconData.svg;
      const category = iconData.category || 'uncategorized';
      const tags = iconData.tags || [];
      const code = iconData.code || '';
      const unicode = iconData.unicode || '';
      const pro = iconData.pro || false;

      if (await isDuplicate(name)) {
        duplicateCount++;
        return;
      }

      const response = await axios.get(svgUrl, { responseType: 'text' });
      const svgContent = response.data;

      const iconPath = path.join(targetImagePath, `${name}.svg`);
      await writeFile(iconPath, svgContent);

      processedIcons.push({
        name,
        svg: svgContent,
        category,
        style,
        tags,
        code,
        unicode,
        pro
      });

      if (index === processedIcons.length - 1) {
        await writeDuplicatesFile();
        callback(null, processedIcons.sort((a, b) => a.name.localeCompare(b.name)));
      }
    } catch (error) {
      console.error(`Error processing icon ${name}:`, error.message);
      callback(error, null);
    }
  }

  async function isDuplicate(name) {
    if (!await exists(path.join(targetImagePath, 'duplicates.json'))) {
      return false;
    }

    const data = await readFile(path.join(targetImagePath, 'duplicates.json'), 'utf-8');
    const duplicates = JSON.parse(data);
    return duplicates.includes(name);
  }

  async function writeDuplicatesFile() {
    const duplicates = processedIcons.map(icon => icon.name).filter((name, index, self) => self.indexOf(name) !== index);
    await writeFile(path.join(targetImagePath, 'duplicates.json'), JSON.stringify(duplicates, null, 2));
  }

  async function fetchIconData() {
    try {
      await mkdirp(targetImagePath);
      const response = await axios.get(url, {
        headers: {
          'Accept': 'application/json',
          'credentials': 'include'
        }
      });
      const icons = response.data.icons || [];

      for (let i = 0; i < icons.length; i++) {
        await processIcon(icons[i], i);
      }
    } catch (error) {
      console.error('Error fetching icon data:', error.message);
      callback(error, null);
    }
  }

  fetchIconData();
}

