function getNote(url, instrument, name, audioContext) {
  const cacheKey = `${instrument}-${name}`;
  if (getNote.cache && getNote.cache[cacheKey]) {
    return Promise.resolve(getNote.cache[cacheKey]);
  }

  return new Promise((resolve, reject) => {
    const request = new XMLHttpRequest();
    request.open('GET', `${url}/${instrument}/${name}.wav`, true);
    request.responseType = 'arraybuffer';

    request.onload = () => {
      if (request.status === 200 || request.status === 0) {
        audioContext.decodeAudioData(request.response, (audioBuffer) => {
          const result = { instrument, name, status: 'loaded', audioBuffer };
          if (!getNote.cache) getNote.cache = {};
          getNote.cache[cacheKey] = result;
          resolve(result);
        }, (error) => {
          reject(new Error(`Failed to decode audio data: ${error.message}`));
        });
      } else {
        reject(new Error(`Failed to load audio file: ${request.statusText}`));
      }
    };

    request.onerror = () => {
      reject(new Error(`Network error loading audio file`));
    };

    request.send();
  });
}

