    return new Promise((resolve, reject) => {
      if (method === 'get') {
        this.requestMethod.get(url, { headers }).then(resolve).catch(reject);
      } else if (method === 'post') {
        this.requestMethod.post(url, data, { headers, requestType }).then(resolve).catch(reject);
      } else if (method === 'put') {
        this.requestMethod.put(url, data, { headers }).then(resolve).catch(reject);
      } else {
        reject(new Error('Unsupported request method'));
      }
    });
  }
}

