class URLExpander {
  constructor() {
    this.map = {
      "is.gd": "is.gd",
      "goo.gl": "goo.gl",
      "budurl.com": "budurl.com",
      "snipurl.com": "snipurl.com"
    };
    this.timeout = 5000;
    this.APIs = {
      "is.gd": (url, callback) => {
        const request = require('request');
        request.get(`http://is.gd/api.php?longurl=${encodeURIComponent(url)}`, { timeout: this.timeout }, (err, res, body) => {
          if (err || res.statusCode !== 200) {
            return callback({ err: err || new Error(`HTTP ${res.statusCode}`) });
          }
          callback({ url: body.trim() });
        });
      },
      "goo.gl": (url, callback) => {
        const request = require('request');
        request.get(`https://www.googleapis.com/urlshortener/v1/url?shortUrl=${encodeURIComponent(url)}&key=YOUR_API_KEY`, { timeout: this.timeout }, (err, res, body) => {
          if (err || res.statusCode !== 200) {
            return callback({ err: err || new Error(`HTTP ${res.statusCode}`) });
          }
          const data = JSON.parse(body);
          callback({ url: data.longUrl });
        });
      },
      "budurl.com": (url, callback) => {
        const request = require('request');
        request.get(`http://budurl.com/api.php?longurl=${encodeURIComponent(url)}`, { timeout: this.timeout }, (err, res, body) => {
          if (err || res.statusCode !== 200) {
            return callback({ err: err || new Error(`HTTP ${res.statusCode}`) });
          }
          callback({ url: body.trim() });
        });
      },
      "snipurl.com": (url, callback) => {
        const request = require('request');
        request.get(`http://snipurl.com/api.php?longurl=${encodeURIComponent(url)}`, { timeout: this.timeout }, (err, res, body) => {
          if (err || res.statusCode !== 200) {
            return callback({ err: err || new Error(`HTTP ${res.statusCode}`) });
          }
          callback({ url: body.trim() });
        });
      }
    };
  }

  expand(args, callback) {
    const { url, depth = 0, seen = {} } = args;
    if (depth > 10) {
      return callback({ err: new Error("Maximum recursion depth exceeded") });
    }
    if (seen[url]) {
      return callback({ err: new Error("Cycle detected in URL expansion") });
    }
    seen[url] = true;

    const domain = new URL(url).hostname;
    if (this.map[domain]) {
      this.APIs[domain](url, (result) => {
        if (result.err) {
          return callback({ ...args, err: result.err });
        }
        this.expand({ url: result.url, depth: depth + 1, seen }, callback);
      });
    } else {
      const request = require('request');
      request.head(url, { followRedirect: false, timeout: this.timeout }, (err, res) => {
        if (err) {
          return callback({ ...args, err });
        }
        if (res.headers.location) {
          this.expand({ url: res.headers.location, depth: depth + 1, seen }, callback);
        } else {
          callback({ ...args, url, headers: res.headers });
        }
      });
    }
  }
}

